mapper.update:
update budget
budget_date=#{budgetDate}, budget=#{budget}, food_expenses=#{foodExpenses}, dwelling_communication=#{dwellingCommunication}, householdgoods=#{householdgoods}, cloth_beauty=#{clothBeauty}, health_culture=#{healthCulture}, education_parenting=#{educationParenting}, traffic_vehicle=#{trafficVehicle}, holiday_dues=#{holidayDues}, tax_interest=#{taxInterest}, pinmoney_etc=#{pinmoneyEtc}, saving_insurance=#{savingInsurance}, credit_card=#{creditCard}, unclassified=#{unclassified}
where member_id=#{memberId} and num=#{num}

mapper.select:
select *
from budget
where member_id=#{member_id}
and budget_date=#{budgetDate}
