mapper.update:
update free_board
set title=#{title},
	content=#{content},
	category=#{category},
	file_root=#{fileRoot}
where no=#{no}

update free_board
set click=click +1
where no=#{no}

update free_board
set good=good+1
where no=#{no}

mapper.select:
select no, title,member_id memberId,register_time registerTime,click,good 
from(
		select ceil(rownum/#{current}) page,no, title,member_id,register_time,click,good 
		from free_board
)
where page=#{page}

select no,title,member_id memberId,register_time registerTime,click,good,content,file_root fileRoot
from free_board
where no=#{no}

select count(id)
from comments
where font_no =#{value}
