mapper.update:
update free_board
set title=#{title},
	content=#{content},
	file_root=#{fileName}
where no=#{no}

update free_board
set click=click +1
where no=#{no}

update free_board
set good=good+#{num}
where no=#{no}

mapper.select:
select f.no,f.title,f.member_id ,f.register_time ,f.click,f.good,f.content,f.file_root 
,c.id,c.comment_content,c.comment_member_id ,c.no,c.comment_group ,c.group_level ,c.comment_register_time 
from free_board f,(
			select id,no,comment_content,comment_member_id ,comment_group ,group_level ,comment_register_time 
			from free_comments
			where no=#{value} 
			order by comment_group,id 
		) c
where f.no=#{value}
and f.no=c.no(+)

select no, title,member_id memberId,register_time registerTime,click,good,file_root fileName 
from(
		select ceil(rownum/#{current}) page,no, title,member_id,register_time,click,good,file_root 
		from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
		)
)
where page=#{page}

select  count(no)
from free_board
where member_id=#{value}

select no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,no,title,member_id,register_time,click,good,file_root
	from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
	)
	where member_id=#{memberId}
)
where page=#{page}

select no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,no,title,member_id,register_time,click,good,file_root 
	from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
	)
	where title like '%'||#{title}||'%'
)
where page=#{page}
