mapper.update:
update tip_board
set click=#{click}
where no=#{no}

update tip_board
set good=#{good}
where no=#{no}

mapper.select:
select classification,no,title,member_id memberId,register_time registerTime,click,good
from (
	select ceil(rownum/#{currentPage}) page,no,classification,title,member_id,register_time,click,good
	from tip_board
)
where page=#{page}

select classification,title,register_time registerTime, member_id memberId,file_root fileRoot,content,click
from tip_board
where no=#{no}

select count(no)
from comment
where font_no=#{value}

select no, title,member_id memberId,register_time registerTime,click,good,file_root fileName 
from(
		select ceil(rownum/#{current}) page,no, title,member_id,register_time,click,good,file_root 
		from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
		)
)
where page=#{page}

select  count(no)
from free_board
where member_id=#{value}

select no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,no,title,member_id,register_time,click,good,file_root
	from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
	)
	where member_id=#{memberId}
)
where page=#{page}

select no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,no,title,member_id,register_time,click,good,file_root 
	from (
			select no,title,member_id,register_time,click,good,file_root
			from free_board
			order by no desc
	)
	where title like '%'||#{title}||'%'
)
where page=#{page}
