mapper.update:
update tip_board 
set title=#{title},
	content=#{content},
	file_root=#{fileName},
	classification=#{classification}
where no=#{no}

update tip_board
set click=click+1
where no=#{no}

update tip_board
set good=good+#{num}
where no=#{no}

mapper.select:
select classification,no,title,member_id memberId,register_time registerTime,click,good
from (
	select ceil(rownum/#{currentPage}) page,no,classification,title,member_id,register_time,click,good
	from tip_board
)
where page=#{page}

select no,classification,title,register_time registerTime, member_id memberId,file_root fileName,content,click
from tip_board
where no=#{no}

select classification,no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,classification,no,title,member_id,register_time,click,good,file_root 
	from (
			select classification,no,title,member_id,register_time,click,good,file_root
			from tip_board
			where title like '%'||#{title}||'%'
			order by no desc
	)
)
where page=#{page}

select  count(no)
from tip_board
where member_id=#{value}

select classification,no,title,member_id memberId,register_time registerTime,click,good,file_root fileName
from(
	select ceil(rownum/#{current}) page,classification,no,title,member_id,register_time,click,good,file_root
	from (
			select classification,no,title,member_id,register_time,click,good,file_root
			from tip_board
			where member_id=#{memberId}
			order by no desc
	)
)
where page=#{page}

select count(no)
from tip_board
where classification=#{value}

select classification,no,title,memberId,registerTime,click,good
from(	
	select ceil(rownum/#{current}) page,classification,no,title,memberId,registerTime,click,good
	from(
		select classification,no,title,member_id memberId,register_time registerTime,click,good
		from tip_board
		where classification=#{classification}
		order by no desc
		)
	)	
where page=#{page}
